var title=document.getElementById('title');document.getElementsByTagName('p');function getElementsByClassName(node,classname){if(node.getElementByClassName){return node.getElementsByClassName(classname)}else{var result=[];var elems=node.getElementByTagName('*');for(var i=0,len=elems.length;i<len;i+=1){if(elems[i].className.indexOf(classname)!=-1){result.push(elems[i])}}return result}}console.log(title);title.setAttribute('title',"hcxowe");console.log(title.getAttribute('title'));